import React, { useState } from 'react'



export default function Login() {

	const [email, setEmail] = useState("");
	const [password, setPassword] = useState("");

	const userData = {
	  email,
	  password,
	};
const  sendLoginData = async (object) => {
		const user = await fetch("http://localhost:3000/login", {
		  method: "POST",
		  headers: { "Content-type": "application/json" },
		  body: JSON.stringify(object),
		});

		if (user.status === 200) {
		  const userJson = await user.json();
		  const token = userJson.enterToken;
		  const id = userJson.id;
		  localStorage.setItem("jsonwebtoken", token);
		  localStorage.setItem("id", id);
		  console.log(userJson);

		} else {
		  alert("Invalid Email And Or Password");
		}
	  };

	return (
		<div>

			<input type="text" name="email"  placeholder="email" value={email}
            onChange={(e) => setEmail(e.target.value)}/>
			<input type="text" name="password" placeholder="password" value={password}
            onChange={(e) => setPassword(e.target.value)} />
			<button  onClick={() => sendLoginData(userData)}>SUBMIT</button>

		</div>
	)
	}
