import React, {useState, useEffect} from 'react'
import {firstName} from "../actions/Registration-actions"
import { useSelector, useDispatch } from 'react-redux'
export default function Register() {
	const dispatch = useDispatch()
	// const [firstname, setFirstName] = useState([]);
	// const [lastname, setLastName] = useState([]);
	// const [email, setEmail] = useState([]);
	// const [password, setPassword] = useState([]);

const [inputs, setInputs] = useState({
	firstname: '',
	lastname: '',
	email: '',
	password: '',
});

const {
	firstname,
	lastname,
	email,
	password
} = inputs;

const onChange = (e) => {
	setInputs({
		...inputs,
		[e.target.name]: e.target.value
	});
};

const onSubmitForm = async (e) => {
	e.preventDefault();

		const body = {
			firstname,
			lastname,
			email,
			password
		};

		const response = await fetch('http://localhost:3006/register',
		{
			method: 'POST',
			headers: {
				'Content-type': 'application/json',
			},
			body: JSON.stringify(body)


		}



	);
	}




	return (
		<div>
			<form onSubmit={onSubmitForm}>
		<input type="text" placeholder="firstname" value={firstname}  onChange={(e)=> onChange(e)}/>
		<input type="text" placeholder="lastname"  value={lastname} onChange={(e)=> onChange(e)}/>
		<input type="email" placeholder="email"  value={email}  onChange={(e)=> onChange(e)}/>
		<input type="password" placeholder="password"  value={password}  onChange={(e)=> onChange(e)} />
		<button >SUBMIT</button>
			</form>
		</div>
	)

	}
