import React from 'react'
import {TotalDivStyle} from "../styled-components/TotalStyle"
import { useSelector } from 'react-redux';
import {TotalH1} from "../styled-components/TotalStyle"
export default function Total() {
	const cart = useSelector((state) => state.Cart);
	let total=0;
	let formatter = new Intl.NumberFormat('en-US', {
		style: 'currency',
		currency: "USD"
	});

	for (const subtotal in cart) {
		total += cart[subtotal].price * cart[subtotal].quantity
	}
	return (
		<TotalDivStyle>
			<TotalH1>Total: {formatter.format(total)}</TotalH1>
		<button>Check-Out</button>

		</TotalDivStyle>
	)
}
