import React, { useState } from 'react'



function Login() {
	const [email, setEmail] = useState("");
	const [password, setPassword] = useState("");


	// const [loginInfo, setLoginInfo] = useState({})
	const userInfo = {
		email, 
		password
	}

	// const handleChange = (e) => {
	// 	setLoginInfo({
	// 		...loginInfo,
	// 		[e.target.name]: [e.target.value]
	// 	})
	// }

	const handleLogin = async() => {
		const user =fetch("http://localhost:3002/login", {
			method: "POST",
			headers: {"Content-type": "application/json"},
			body: JSON.stringify(userInfo)
		})
		if (user.status === 200) {
			const userJson = await user.json();
			const token = userJson.jsonToken;
			const user_id = userJson.user_id;
			localStorage.setItem("jsonwebtoken", token );
			localStorage.setItem("user_id", user_id);
			console.log(userJson)
		}

	}

	return (
		<div>

			<input type="text" name="email"  onChange={(e) => setEmail(e.target.value)} placeholder="email" />
			<input type="text" name="password" onChange={(e) => setPassword(e.target.value)} placeholder="password" />
			<button onClick={() => handleLogin(userInfo)}>SUBMIT</button>

		</div>
	)
	}

export default Login
